const CSS = require('css')
const EOF = Symbol('EOF') // End Of File
const SPACE_ERG = /^[\t\n\f ]$/
const layout = require('./layout')

// 全局变量， 在HTML中TAG不管多复杂，都是当作一个token去处理的
let currentToken = null
let currentAttribute = null
let currentTextNode = null


let stack = [{type: 'document', children: []}]

// add new method: addCSSRules, css规则暂存在一个数组中
let rules = []

function addCSSRules (cssText) {
    var ast = CSS.parse(cssText)
    // console.info('css ast: ', typeof ast, ast.type)
    rules.push(...ast.stylesheet.rules)
}

function match (element, selector) {
    // 若没有选择器或者是文本节点
    if (!selector || !element.attributes) return false
    var attr = null
    if (selector.charAt(0) === '#') {
        attr = element.attributes.filter(attr => attr.name === 'id')[0]
        if (attr && attr.value === selector.replace('#', '')) return true
    } else if (selector.charAt(0) === '.') {
        attr = element.attributes.filter(attr => attr.name === 'class')[0]
        if (attr && attr.value === selector.replace('.', '')) return true
    } else {
        if (element.tagName === selector) return true
    }
    return false
}

function specificity (selector) {
    var p = Array(4).fill(0)
    var selectorParts = selector.split(' ')
    for (var part of selectorParts) {
        if (part.charAt(0) === '#') {
            p[1] += 1
        } else if (part.charAt(0) === '.') {
            p[2] += 1
        } else {
            p[3] += 1
        }
    }
    return p
}

function compare (sp1, sp2) {
    if (sp1[0] - sp2[0])
        return sp1[0] - sp2[0]
    if (sp1[1] - sp2[1])
        return sp1[1] - sp2[1]
    if (sp1[2] - sp2[2])
        return sp1[2] - sp2[2]
    return sp1[3] - sp2[3]
}

function computeCSS (element) {
    console.info(element)
    console.info('Compute CSS for Element', element)
    // 获取父元素序列
    var elements = stack.slice().reverse()
    if (!element.computedStyle)
        element.computedStyle = {}

    for (let rule of rules) {
        var selectorParts = rule.selectors[0].split(' ').reverse()

        if (!match(element, selectorParts[0]))
            continue

        var j = 1
        for (var i = 0; i < elements.length; i++) {
            if (match(elements[i], selectorParts[j])) {
                j++
            }
        }

        if (j >= selectorParts.length) {
            console.info('Element', element, 'matched rule', rule)

            var sp = specificity(rule.selectors[0])
            var computedStyle = element.computedStyle
            for (var declaration of rule.declarations) {
                if (!computedStyle[declaration.property])
                    computedStyle[declaration.property] = {}
                if (!computedStyle[declaration.property].specificity) {
                    computedStyle[declaration.property].value = declaration.value
                    computedStyle[declaration.property].specificity = sp
                } else if (compare(computedStyle[declaration.property].specificity, sp) < 0) {
                    computedStyle[declaration.property].value = declaration.value
                    computedStyle[declaration.property].specificity = sp
                }
            }
        }

    }
}

function emit (token) {
    // console.info(token)
    // if (token.type === 'text') return
    let top = stack[stack.length - 1]
    if (token.type === 'startTag') {
        // 写在html文本里面的带尖括号的叫tag，它背后表示的就是element，所以document对象里面只会有node和element不会有tag
        let element = {
            type: 'element',
            children: [],
            attributes: []
        }

        element.tagName = token.tagName

        for (let p in token) {
            if (p !== 'type' && p !== 'tagName') {
                element.attributes.push({
                    name: p,
                    value: token[p]
                })
            }
        }

        computeCSS(element)

        top.children.push(element)
        element.parent = top
        if (!token.isSelfClosing)
            stack.push(element)
        currentTextNode = null
    } else if (token.type === 'endTag') {
        if (top.tagName !== token.tagName) {
            throw new Error('tag start end doesn`t match')
        } else {
            // 遇到style标签时，执行添加css规则的操作
            if (top.tagName === 'style') {
                addCSSRules(top.children[0].content)
            }
            // 排版发生在标签的结束标签之前
            layout(top)
            stack.pop()
        }
        currentTextNode = null
    } else if (token.type === 'text') {
        if (currentTextNode === null) {
            currentTextNode = {
                type: 'text',
                content: ''
            }
            top.children.push(currentTextNode)
        }
        currentTextNode.content += token.content
    }
}

function data (c) {
    if (c === '<') {
        return tagOpen
    } else if (c === EOF) {
        emit({
            type: 'EOF'
        })
        return
    } else {
        emit({
            type: 'text',
            content: c
        })
        return data
    }
}

function tagOpen(c) {
    if (c === '/') {
        return endTagOpen
    } else if (c.match(/^[a-zA-Z]$/)) {
        currentToken = {
            type: 'startTag',
            tagName: ''
        }
        return tagName(c)
    } else {
        // 标签以特殊字符开头
        return
    }
}

function endTagOpen (c) {
    if (c.match(/^[a-zA-Z]$/)) {
        currentToken = {
            type: 'endTag',
            tagName: ''
        }
        return tagName(c)
    } else if (c === '>') {
        emit(currentToken)
    } else if (c === EOF) {
    } else {
    }
}

function tagName(c) {
    if (c.match(SPACE_ERG)) {
        return beforeAttributeName
    } else if (c === '/') {
        return selfClosingStartTag
    } else if (c.match(/^[a-zA-Z]$/)) {
        currentToken.tagName += c//.toLowerCase()
        return tagName
    } else if (c === '>') {
        emit(currentToken)
        return data
    } else {
        // 标签名称里面包含特殊字符才会触发这个else
        currentToken.tagName += c
        return tagName
    }
}

function beforeAttributeName (c) {
    if (c.match(SPACE_ERG)) {
        return beforeAttributeName
    } else if (c ==='/' || c === '>' || c === EOF) {
        return afterAttributeName(c)
    } else if (c === '=') {
        // return error
    } else {
        currentAttribute = {
            type: '',
            value: '',
            name: ''
        }
        return attributeName(c)
    }
}

function attributeName (c) {
    if (c.match(SPACE_ERG) || c === '>'|| c === '/'|| c === EOF) {
        return afterAttributeName(c)
    } else if (c === '=') {
        return beforeAttributeValue
    } else if (c === '\u0000') {
    } else if (c === "\'" || c === "'" || c === '<') {
    } else {
        currentAttribute.name += c
        return attributeName
    }
}

function beforeAttributeValue (c) {
    if (c.match(SPACE_ERG) || c === '>'|| c === '/'|| c === EOF) {
        return beforeAttributeValue
    } else if (c === "\"") {
        return doubleQuotedAttributeValue
    } else if (c === "\'") {
        return singleQuotedAttributeValue
    } else if (c === '>') {
        // return data
    } else {
        return UnquotedAttributeValue(c)
    }
}

function doubleQuotedAttributeValue (c) {
    if (c === "\"") {
        currentToken[currentAttribute.name] = currentAttribute.value
        return afterQuotedAttributeValue
    } else if (c === '\u0000') {
    } else if (c === EOF) {
    } else {
        currentAttribute.value += c
        return doubleQuotedAttributeValue
    }
}
function singleQuotedAttributeValue (c) {
    if (c === "\'") {
        currentToken[currentAttribute.name] = currentAttribute.value
        return afterQuotedAttributeValue
    } else if (c === '\u0000') {
    } else if (c === EOF) {
    } else {
        currentAttribute.value += c
        return singleQuotedAttributeValue
    }
}

function afterQuotedAttributeValue (c) {
    if (c.match(SPACE_ERG)) {
        return beforeAttributeName
    } else if (c === '/') {
        return selfClosingStartTag
    } else if (c === '>') {
        currentToken[currentAttribute.name] = currentAttribute.value
        emit(currentToken)
        return data
    } else if (c === '\u0000') {
    } else if (c === EOF) {
    } else {
        currentAttribute.value += c
        return UnquotedAttributeValue
    }
}

function UnquotedAttributeValue (c) {
    if (c.match(SPACE_ERG)) {
        currentToken[currentAttribute.name] = currentAttribute.value
        return beforeAttributeName
    } else if (c === '/') {
        currentToken[currentAttribute.name] = currentAttribute.value
        return selfClosingStartTag
    } else if (c === '>') {
        currentToken[currentAttribute.name] = currentAttribute.value
        emit(currentToken)
        return data
    } else if (c === '\u0000') {
    } else if (c === "\"" || c === "'" || c === "<" || c === "=" || c === "`") {
    } else if (c === EOF) {
    } else {
        currentAttribute.value += c
        return UnquotedAttributeValue
    }
}
function afterAttributeName (c) {
    if (c.match(SPACE_ERG)) {
        return afterAttributeName
    } else if (c === '/') {
        return selfClosingStartTag
    } else if (c === '=') {
        return beforeAttributeValue
    } else if (c === '>') {
        currentToken[currentAttribute.name] = currentAttribute.value
        emit(currentToken)
        return data
    } else if (c === EOF) {
    } else {
        currentToken[currentAttribute.name] = currentAttribute.value
        currentAttribute = {
            name: '',
            value: ''
        }
        return attributeName(c)
    }
}

function selfClosingStartTag (c) {
    if (c === '>') {
        currentToken.isSelfClosing = true
        emit(currentToken)
        return data
    }
}

// module.exports.parseHTML = function parseHTML(html) {
export function parseHTML (html) {
    let state = data
    for (let c of html) {
        state = state(c)
    }
    state = state(EOF)
    return stack[0]
}

// let htmlText = `<html>
// <head>
//   <title>vue-g</title>
//   <style>
//     .test-link {
//         color: red;
//         font-size: 20px;
//     }
//   </style>
// </head>
// <body>
//   <div%
//     data-range
//     isError='false'
//     id="app"
//     class="test-link"
//   >
//   </div%>
//   <br/>
//   <img src="https://zh.wikipedia.org/wiki/File:Andy_Lau_(cropped).jpg" />
// </body>
// </html>`

// let res = parseHTML(htmlText)

// console.info('cur-page', res)

function getStyle (element) {
    if (!element.style)
        element.style = {}
    for (let prop in element.computedStyle) {
        let p = element.computedStyle.value
        element.style[prop] = element.computedStyle[prop].value

        if (element.style[prop].toString().match(/px$/)) {
            element.style[prop] = parseInt(element.style[prop])
        }
    
        if (element.style[prop].toString().match(/^[0-9\.]+$/)) {
            element.style[prop] = parseInt(element.style[prop])
        }
    }

    return element.style
}

function layout (element) {
    if (!element.computedStyle)
        return

    var elementStyle = getStyle(element)

    if (elementStyle.display !== 'flex')
        return

    var items = element.children.filter(e => e.type === 'element')

    items.sort(function (a, b) {
        return (a.order || 0) - (b.order || 0)
    })

    var style = elementStyle

    ['width', 'height'].forEach(size => {
        if (style[size] === 'auto' || style[size] === '') {
            style[size] = null
        }       
    })

    if (!style.flexDirection || style.flexDirection === 'auto')
        style.flexDirection = 'row'
    if (!style.alignItems || style.alignItems === 'auto')
        style.alignItems = 'stretch'
    if (!style.justifyContent || style.justifyContent === 'auto')
        style.justifyContent = 'flex-start'
    if (!style.flexWrap || style.flexWrap === 'auto')
        style.flexWrap = 'nowrap'
    if (!style.alignContent || style.alignContent === 'auto')
        style.alignContent = 'stretch'

    var mainSize, mainStart, mainEnd, mainSign, mainBase,
        crossSize, crossStart, crossEnd, crossSign, crossBase
    if (style.flexDirection === 'row') {
        mainSize = 'width'
        mainStart = 'left'
        mainEnd = 'right'
        mainSign = +1
        mainBase = 0
        crossSize = 'height'
        crossStart = 'top'
        crossEnd = 'bottom'
    }
    if (style.flexDirection === 'row-reverse') {
        mainSize = 'width'
        mainStart = 'right'
        mainEnd = 'left'
        mainSign = -1
        mainBase = style.width
        crossSize = 'height'
        crossStart = 'top'
        crossEnd = 'bottom'
    }
    if (style.flexDirection === 'column') {
        mainSize = 'height'
        mainStart = 'top'
        mainEnd = 'bottom'
        mainSign = +1
        mainBase = 0
        crossSize = 'width'
        crossStart = 'left'
        crossEnd = 'right'
    }
    if (style.flexDirection === 'column-reverse') {
        mainSize = 'height'
        mainStart = 'bottom'
        mainEnd = 'top'
        mainSign = -1
        mainBase = style.height
        crossSize = 'width'
        crossStart = 'left'
        crossEnd = 'right'
    }

    // 交叉轴只受wrap-reverse的影响
    if (style.flexWrap === 'wrap-reverse') {
        var tmp = crossStart
        crossStart = crossEnd
        crossEnd = tmp
        crossSign = -1
    } else {
        crossBase = 0
        crossSign = +1
    }

    var isAutoMainSize = false
    if (!style[mainSize]) {
        elementStyle[mainSize] = 0
        for (var i = 0; i< items.length; i++) {
            var item = items[i]
            var itemStyle = getStyle(item)
            if (itemStyle[mainSize] !== null && itemStyle[mainSize] !== (void 0)) {
                elementStyle[mainSize] += itemStyle[mainSize]
            }
        }
        isAutoMainSize = true
    }

    var flexLine = []
    var flexLines = [flexLine]

    var mainSpace = elementStyle[mainSize]
    var crossSpace = 0

    for (var i = 0; i < items.length; i++) {
        var item = items[i]
        var itemStyle = getStyle(item)

        if (itemStyle[mainSize] === null) {
            itemStyle[mainSize] = 0
        }

        if (itemStyle.flex) {
            flexLine.push(item)
        } else if (style.flexWrap === 'nowrap' && isAutoMainSize) {
            mainSpace -= itemStyle[mainSize]
            if (itemStyle[crossSize] !== null && itemStyle[crossSize] !== (void 0)) {
                crossSpace = Math.max(crossSpace, itemStyle[crossSpace])
            }
            flexLine.push(item)
        } else {
            if (itemStyle[mainSize] > style[mainSize]) {
                itemStyle[mainSize] = style[mainSize]
            }
            if (mainSpace < itemStyle[main]) {
                flexLine.mainSpace = mainSpace
                flexLine.crossSpace = crossSpace
                flexLine = [item]
                flexLines.push(flexLine)
                // reset mainSpace crossSpace
                mainSpace = style[mainSize]
                crossSpace = 0
            } else {
                flexLine.push(item)
            }
            if (itemStyle[crossSize] !== null && itemStyle[crossSize] !== (void 0)) {
                crossSpace = Math.max(crossSpace, itemStyle[crossSpace])
            }
            mainSpace -= itemStyle[mainSize]
        }
    }
    // 主轴方向的计算主要是分配mainSpace
    flexLine.mainSpace = mainSpace

    console.info(items)
    if (style.flexWrap === 'nowrap' || isAutoMainSize) {
        flexLine.crossSpace = (style[crossSize] !== (void 0)) ? style[crossSize] : crossSpace
    } else {
        flexLine.crossSpace = crossSpace
    }

    if (mainSpace < 0) {
        // 等比压缩
        var scale = style[mainSize] / (style[mainSize] - mainSpace)
        var currentMain = mainBase
        for (var i = 0; i < items.length; i++) {
            var item = items[i]
            var itemStyle = getStyle(item)

            if (itemStyle.flex) {
                itemStyle[mainSize] = 0
            }

            itemStyle[mainSize] = mainSize * scale

            itemStyle[mainStart] = currentMain
            itemStyle[mainEnd] = itemStyle[mainStart] + mainSign * itemStyle[mainSize]
            currentMain = itemStyle[mainEnd]
        }
    } else {
        flexLines.forEach(function (items) {
            var mainSpace = mainSpace
            var flexTotal = 0
            for (var i = 0; i < items.length; i++) {
                var item = items[i]
                var itemStyle = getStyle(item)

                if (itemStyle.flex !== null && itemStyle.flex !== (void 0)) {
                    flexTotal += itemStyle.flex
                    continue
                }
            }

            if (flexTotal > 0) {
                var currentMain = mainBase
                for (var i = 0; i < items.length; i++) {
                    var item = items[i]
                    var itemStyle = getStyle(item)
                     if (itemStyle.flex) {
                         itemStyle[mainSize] = (mainSpace / flexTotal) * itemStyle.flex
                     }
                     itemStyle[mainStart] = currentMain
                     itemStyle[mainEnd] = itemStyle[mainStart] + mainSign * itemStyle[mainSize]
                     currentMain = itemStyle[mainEnd]
                }
            } else {
                if (style.justifyContent === 'flex-start') {
                    var currentMain = mainBase
                    var step = 0
                }
                if (style.justifyContent === 'flex-end') {
                    var currentMain = mainSpace * mainSign + mainBase
                    var step = 0
                }
                if (style.justifyContent === 'center') {
                    var currentMain = mainSpace / 2 * mainSign + mainBase
                    var step = 0
                }
                if (style.justifyContent === 'space-between') {
                    var step = mainSpace / (items.length - 1) + mainBase
                    var currentMain = mainBase
                }
                if (style.justifyContent === 'space-around') {
                    var step = mainSpace / items.length + mainBase
                    var currentMain = step / 2 + mainBase
                }
                for (var i = 0; i < items.length; i++) {
                    var item = items[i]
                    var itemStyle = getStyle(item)
                    itemStyle[mainStart] = currentMain
                    itemStyle[mainEnd] = itemStyle[mainStart] + mainSign * itemStyle[mainSize]
                    currentMain = itemStyle[mainEnd] + step
                }
            }
        })
    }

    // 交叉轴方向的计算
    var crossSpace
    if (!style[crossSpace]) {
        crossSpace = 0
        elementStyle[crossSize] = 0
        for (var i = 0; i < flexLines.length; i++) {
            elementStyle[crossSize] = elementStyle[crossSize] + flexLines[i].crossSize
        }
    } else {
        crossSpace = style[crossSize]
        for (var i = 0; i < flexLines.length; i++) {
            crossSpace -= flexLines[i].crossSpace
        }
    }

    if (style.flexWrap === 'wrap-reverse') {
        crossBase = style[crossSize]
    } else {
        crossBase = 0
    }

    var lineSize = style[crossSize] / flexLines.length
    var step
    if (style.alignContent === 'flex-start') {
        crossBase += 0
        step = 0
    }
    if (style.alignContent === 'flex-end') {
        crossBase += crossSign * crossSpace
        step = 0
    }
    if (style.alignContent === 'center') {
        crossBase += crossSign * crossSpace / 2
        step = 0
    }
    if (style.alignContent === 'space-between') {
        crossBase += 0
        step = crossSpace / (flexLines.length - 1)
    }
    if (style.alignContent === 'space-around') {
        step = crossSpace / flexLines.length
        crossBase += crossSign * step / 2
    }
    if (style.alignContent === 'stretch') {
        crossBase += 0
        step = 0
    }
    flexLines.forEach(function (items) {
        var lineCrossSize = style.alignContent === 'stretch' ?
            items.crossSpace + crossSpace / flexLines.length :
            items.crossSpace
        for (var i = 0; i < items.length; i++) {
            var item = items[i]
            var itemStyle = getStyle(item)

            var align = itemStyle.alignSelf || style.alignItems

            if (item === null) {
                itemStyle[crossSize] = align === 'stretch' ? lineCrossSize : 0
            }

            if (align === 'flex-start') {
                itemStyle[crossStart] = crossBase
                itemStyle[crossEnd] = itemStyle[crossStart] + crossSign * itemStyle[crossSize]
            }

            if (align === 'flex-end') {
                itemStyle[crossEnd] = crossBase + crossSign * lineCrossSize
                itemStyle[crossStart] = itemStyle[crossEnd] + crossSign * itemStyle[crossSize]
            }

            if (align === 'center') {
                itemStyle[crossStart] = crossBase + crossSign * (lineCrossSize - itemStyle[crossSize] / 2)
                itemStyle[crossEnd] = itemStyle[crossStart] + crossSign * itemStyle[crossSize]
            }

            if (align === 'stretch') {
                itemStyle[crossStart] = crossBase
                itemStyle[crossEnd] = itemStyle[crossStart] + crossSign * ((itemStyle[crossSize] !== null && itemStyle[crossSize] !== (void 0)) ? itemStyle[crossSize] : lineCrossSize)
                itemStyle[crossSize] = crossSign * (itemStyle[crossEnd] - itemStyle[crossStart])
            }
        }
        crossBase += crossSign * (lineCrossSize + step)
    })
}

module.exports = layout

/**
 * 一. 准备工作
 * 1.处理掉flexDirection 和 wrap相关的属性
 * 2.把具体的height，width等属性抽象成了main，cross相关的属性
 * 
 * 二. 收集元素进行
 * 1.根据主轴尺寸，把元素进行分行
 * 2.若设置了no-wrap，强行分配进第一行
 * 
 * 三. 计算主轴方向
 * 1.找出所有Flex元素
 * 2.把主轴方向的剩余尺寸按比例分配给这些元素
 * 3.若剩余空间为负数，所有flex元素为0，等比压缩剩余元素
 * 
 * 四. 计算交叉轴方向
 * 1.根据每一行中最大元素尺寸计算行高
 * 2.根据行高flex-align 和 item-align，确定元素具体位置
 * 
 * 
 */
